const seedOutlets = async () => {
  try {
    console.log('🌱 Starting to seed outlets...');
    
    // Clear existing outlets
    await Outlet.deleteMany({});
    console.log('🗑️  Cleared existing outlets');
    
    // Insert sample outlets
    const createdOutlets = await Outlet.insertMany(sampleOutlets);
    console.log(`✅ Successfully created ${createdOutlets.length} outlets:`);
    
    createdOutlets.forEach(outlet => {
      const type = outlet.isCentralKitchen ? '🏭 Central Kitchen' : '🏪 Regular Outlet';
      console.log(`   - ${outlet.outletCode}: ${outlet.outletName} (${type})`);
    });
    
    console.log('\n📊 Outlet Summary:');
    const centralKitchens = createdOutlets.filter(o => o.isCentralKitchen);
    const regularOutlets = createdOutlets.filter(o => !o.isCentralKitchen);
    
    console.log(`   🏭 Central Kitchens: ${centralKitchens.length}`);
    console.log(`   🏪 Regular Outlets: ${regularOutlets.length}`);
    console.log(`   📍 Total Locations: ${createdOutlets.length}`);
    
    console.log('\n🎉 Outlet seeding completed successfully!');
    
  } catch (error) {
    console.error('❌ Error seeding outlets:', error);
  } finally {
    mongoose.connection.close();
    console.log('🔌 Database connection closed');
  }
};

// Run the seeding
connectDB().then(() => {
  seedOutlets();
});
